SELECT COUNT(*) FROM tag AS t, site AS s, question AS q, tag_question AS tq WHERE t.site_id = s.site_id AND q.site_id = s.site_id AND tq.site_id = s.site_id AND tq.question_id = q.id AND tq.tag_id = t.id AND s.site_name IN ('stackoverflow') AND t.name IN ('antlr4', 'cell', 'cordova-plugins', 'database-trigger', 'derby', 'domain-driven-design', 'exchange-server', 'google-maps-markers', 'ios9', 'maven-plugin', 'seaborn', 'xml-serialization') AND q.score >= 0 AND q.score <= 0