SELECT COUNT(*) FROM tag AS t, site AS s, question AS q, tag_question AS tq WHERE t.site_id = s.site_id AND q.site_id = s.site_id AND tq.site_id = s.site_id AND tq.question_id = q.id AND tq.tag_id = t.id AND s.site_name IN ('stackoverflow') AND t.name IN ('client', 'cocos2d-iphone', 'fft', 'javafx', 'out-of-memory', 'preg-replace', 'query-optimization', 'visual-studio') AND q.score >= 1 AND q.score <= 10