SELECT COUNT(*) FROM tag AS t, site AS s, question AS q, tag_question AS tq WHERE t.site_id = s.site_id AND q.site_id = s.site_id AND tq.site_id = s.site_id AND tq.question_id = q.id AND tq.tag_id = t.id AND s.site_name IN ('chemistry') AND t.name IN ('acid-base', 'inorganic-chemistry', 'organic-chemistry', 'physical-chemistry', 'reaction-mechanism', 'thermodynamics') AND q.score >= 0 AND q.score <= 5