SELECT COUNT(*) FROM tag AS t, site AS s, question AS q, tag_question AS tq WHERE t.site_id = s.site_id AND q.site_id = s.site_id AND tq.site_id = s.site_id AND tq.question_id = q.id AND tq.tag_id = t.id AND s.site_name IN ('gaming') AND t.name IN ('league-of-legends', 'minecraft', 'minecraft-commands', 'technical-issues', 'the-elder-scrolls-5-skyrim') AND q.score >= 1 AND q.score <= 10