SELECT COUNT(*) FROM tag AS t, site AS s, question AS q, tag_question AS tq WHERE t.site_id = s.site_id AND q.site_id = s.site_id AND tq.site_id = s.site_id AND tq.question_id = q.id AND tq.tag_id = t.id AND s.site_name IN ('stackoverflow') AND t.name IN ('colors', 'cookies', 'data.table', 'google-drive-api', 'integration', 'interop', 'ios5', 'mvvmcross', 'nuget', 'polymer', 'razor', 'response', 'typescript') AND q.view_count >= 10 AND q.view_count <= 1000