SELECT COUNT(*) FROM tag AS t, site AS s, question AS q, tag_question AS tq WHERE t.site_id = s.site_id AND q.site_id = s.site_id AND tq.site_id = s.site_id AND tq.question_id = q.id AND tq.tag_id = t.id AND s.site_name IN ('stackoverflow') AND t.name IN ('asp.net', 'design-patterns', 'ios', 'linux', 'numpy', 'php', 'python-3.x', 'r', 'shell', 'sql', 'xml') AND q.favorite_count >= 5 AND q.favorite_count <= 5000