SELECT COUNT(*) FROM tag AS t, site AS s, question AS q, tag_question AS tq WHERE t.site_id = s.site_id AND q.site_id = s.site_id AND tq.site_id = s.site_id AND tq.question_id = q.id AND tq.tag_id = t.id AND s.site_name IN ('stackoverflow') AND t.name IN ('asp.net-mvc-3', 'asynchronous', 'callback', 'character-encoding', 'console', 'grails', 'multithreading', 'nlp', 'query-optimization', 'selenium', 'selenium-webdriver') AND q.view_count >= 0 AND q.view_count <= 100