SELECT COUNT(*) FROM tag AS t, site AS s, question AS q, tag_question AS tq WHERE t.site_id = s.site_id AND q.site_id = s.site_id AND tq.site_id = s.site_id AND tq.question_id = q.id AND tq.tag_id = t.id AND s.site_name IN ('stackoverflow') AND t.name IN ('admin', 'alfresco', 'angular-ui-grid', 'apache-spark-mllib', 'arcgis', 'data-mining', 'delimiter', 'prestashop-1.6', 'redmine', 'startup', 'sticky', 'sybase', 'traits') AND q.view_count >= 10 AND q.view_count <= 1000