SELECT COUNT(*) FROM tag AS t, site AS s, question AS q, tag_question AS tq WHERE t.site_id = s.site_id AND q.site_id = s.site_id AND tq.site_id = s.site_id AND tq.question_id = q.id AND tq.tag_id = t.id AND s.site_name IN ('stackoverflow') AND t.name IN ('admob', 'asp-classic', 'azure-devops', 'cypher', 'deserialization', 'google-drive-api', 'gpu', 'icons', 'jsoup', 'microsoft-graph', 'rubygems', 'sql-server-2008-r2', 'statistics', 'time-complexity') AND q.favorite_count >= 0 AND q.favorite_count <= 1