SELECT COUNT(*) FROM tag AS t, site AS s, question AS q, tag_question AS tq WHERE t.site_id = s.site_id AND q.site_id = s.site_id AND tq.site_id = s.site_id AND tq.question_id = q.id AND tq.tag_id = t.id AND s.site_name IN ('stackoverflow') AND t.name IN ('android-adapter', 'asp.net-core-2.0', 'average', 'docusignapi', 'export-to-excel', 'facebook-like', 'hashtable', 'joomla3.0', 'packages', 'scrollbar', 'uigesturerecognizer') AND q.score >= 0 AND q.score <= 0