SELECT COUNT(*) FROM tag AS t, site AS s, question AS q, tag_question AS tq WHERE t.site_id = s.site_id AND q.site_id = s.site_id AND tq.site_id = s.site_id AND tq.question_id = q.id AND tq.tag_id = t.id AND s.site_name IN ('ru') AND t.name IN ('ajax', 'android', 'css3', 'html5', 'java', 'php', 'python', 'qt', 'winforms', 'wordpress', 'веб-программирование') AND q.view_count >= 10 AND q.view_count <= 1000