SELECT COUNT(*) FROM tag AS t, site AS s, question AS q, tag_question AS tq WHERE t.site_id = s.site_id AND q.site_id = s.site_id AND tq.site_id = s.site_id AND tq.question_id = q.id AND tq.tag_id = t.id AND s.site_name IN ('stackoverflow') AND t.name IN ('key', 'media-queries', 'microsoft-graph', 'qml', 'sql-server-2016', 'upload', 'windows-10', 'xslt-2.0') AND q.view_count >= 0 AND q.view_count <= 100