SELECT COUNT(*) FROM tag AS t, site AS s, question AS q, tag_question AS tq WHERE t.site_id = s.site_id AND q.site_id = s.site_id AND tq.site_id = s.site_id AND tq.question_id = q.id AND tq.tag_id = t.id AND s.site_name IN ('wordpress') AND t.name IN ('categories', 'custom-post-types', 'custom-taxonomy', 'customization', 'functions', 'php', 'plugins', 'theme-development') AND q.score >= 0 AND q.score <= 0