SELECT COUNT(*) FROM tag AS t, site AS s, question AS q, tag_question AS tq WHERE t.site_id = s.site_id AND q.site_id = s.site_id AND tq.site_id = s.site_id AND tq.question_id = q.id AND tq.tag_id = t.id AND s.site_name IN ('stackoverflow') AND t.name IN ('axapta', 'compact-framework', 'endianness', 'excel-2010', 'grammar', 'materialize', 'obfuscation', 'redhat', 'scenekit', 'system.reactive', 'x509certificate') AND q.score >= 1 AND q.score <= 10