SELECT COUNT(*) FROM tag AS t, site AS s, question AS q, tag_question AS tq WHERE t.site_id = s.site_id AND q.site_id = s.site_id AND tq.site_id = s.site_id AND tq.question_id = q.id AND tq.tag_id = t.id AND s.site_name IN ('unix') AND t.name IN ('backup', 'date', 'dns', 'io-redirection', 'keyboard-shortcuts', 'memory', 'sort', 'system-installation', 'upgrade', 'usb', 'vim', 'virtualbox', 'wildcards', 'windows', 'yum') AND q.score >= 0 AND q.score <= 1000