SELECT COUNT(*) FROM tag AS t, site AS s, question AS q, tag_question AS tq WHERE t.site_id = s.site_id AND q.site_id = s.site_id AND tq.site_id = s.site_id AND tq.question_id = q.id AND tq.tag_id = t.id AND s.site_name IN ('stackoverflow') AND t.name IN ('bison', 'devexpress', 'export-to-csv', 'focus', 'gdb', 'ios', 'outlook-vba', 'scroll', 'visual-studio-2005', 'vuex', 'yii2') AND q.score >= 0 AND q.score <= 1000