SELECT COUNT(*) FROM tag AS t, site AS s, question AS q, tag_question AS tq WHERE t.site_id = s.site_id AND q.site_id = s.site_id AND tq.site_id = s.site_id AND tq.question_id = q.id AND tq.tag_id = t.id AND s.site_name IN ('stackoverflow') AND t.name IN ('.net-assembly', 'android-imageview', 'autofac', 'core-location', 'django-queryset', 'forms-authentication', 'log4j2', 'pom.xml', 'spark-dataframe', 'sql-injection', 'system', 'uialertview', 'vuex', 'xdebug', 'xmpp') AND q.score >= 0 AND q.score <= 0