SELECT COUNT(*) FROM tag AS t, site AS s, question AS q, tag_question AS tq WHERE t.site_id = s.site_id AND q.site_id = s.site_id AND tq.site_id = s.site_id AND tq.question_id = q.id AND tq.tag_id = t.id AND s.site_name IN ('stats') AND t.name IN ('anova', 'arima', 'clustering', 'cross-validation', 'data-visualization', 'deep-learning', 'estimation', 'generalized-linear-model', 'maximum-likelihood', 'multiple-regression', 'references', 'statistical-significance', 'variance') AND q.favorite_count >= 0 AND q.favorite_count <= 10000