SELECT COUNT(*) FROM tag AS t, site AS s, question AS q, tag_question AS tq WHERE t.site_id = s.site_id AND q.site_id = s.site_id AND tq.site_id = s.site_id AND tq.question_id = q.id AND tq.tag_id = t.id AND s.site_name IN ('stackoverflow') AND t.name IN ('android-fragments', 'asp.net-mvc-4', 'email', 'firebase', 'forms', 'java-8', 'jpa', 'jsf', 'optimization', 'spring-boot', 'vba', 'visual-studio-2010', 'web-services') AND q.favorite_count >= 5 AND q.favorite_count <= 5000