SELECT COUNT(*) FROM tag AS t, site AS s, question AS q, tag_question AS tq WHERE t.site_id = s.site_id AND q.site_id = s.site_id AND tq.site_id = s.site_id AND tq.question_id = q.id AND tq.tag_id = t.id AND s.site_name IN ('stackoverflow') AND t.name IN ('animation', 'asp.net-mvc-4', 'compiler-construction', 'concurrency', 'fonts', 'garbage-collection', 'ggplot2', 'ios8', 'orm', 'parsing', 'pdf', 'servlets', 'tomcat', 'variables', 'visual-c++') AND q.score >= 10 AND q.score <= 1000