SELECT COUNT(*) FROM tag AS t, site AS s, question AS q, tag_question AS tq WHERE t.site_id = s.site_id AND q.site_id = s.site_id AND tq.site_id = s.site_id AND tq.question_id = q.id AND tq.tag_id = t.id AND s.site_name IN ('electronics') AND t.name IN ('adc', 'analog', 'audio', 'avr', 'circuit-design', 'filter', 'fpga', 'integrated-circuit', 'motor', 'power-electronics', 'pwm', 'relay', 'transformer') AND q.view_count >= 10 AND q.view_count <= 1000