SELECT COUNT(*) FROM tag AS t, site AS s, question AS q, tag_question AS tq WHERE t.site_id = s.site_id AND q.site_id = s.site_id AND tq.site_id = s.site_id AND tq.question_id = q.id AND tq.tag_id = t.id AND s.site_name IN ('math') AND t.name IN ('brownian-motion', 'closed-form', 'convergence', 'definite-integrals', 'factoring', 'finite-groups', 'functional-equations', 'group-theory', 'linear-programming', 'matrices', 'measure-theory', 'physics', 'probability', 'real-numbers') AND q.score >= 0 AND q.score <= 1000