SELECT acc.location, COUNT(*) FROM site AS s, so_user AS u1, question AS q1, answer AS a1, tag AS t1, tag_question AS tq1, badge AS b, account AS acc WHERE s.site_id = q1.site_id AND s.site_id = u1.site_id AND s.site_id = a1.site_id AND s.site_id = t1.site_id AND s.site_id = tq1.site_id AND s.site_id = b.site_id AND q1.id = tq1.question_id AND q1.id = a1.question_id AND a1.owner_user_id = u1.id AND t1.id = tq1.tag_id AND b.user_id = u1.id AND acc.id = u1.account_id AND s.site_name IN ('chemistry', 'cs', 'rpg', 'softwareengineering', 'travel') AND t1.name IN ('dnd-5e', 'organic-chemistry', 'pathfinder-1e', 'spells', 'visas') AND q1.favorite_count >= 0 AND q1.favorite_count <= 1 AND u1.upvotes >= 1 AND u1.upvotes <= 100 AND b.name IN ('Announcer', 'Constituent', 'Enlightened', 'Enthusiast', 'Excavator', 'Favorite Question', 'Good Answer', 'Guru', 'Necromancer', 'Organizer', 'Promoter', 'Quorum', 'Self-Learner') GROUP BY acc.location ORDER BY COUNT(*) DESC LIMIT 100