SELECT COUNT(*) FROM tag AS t, site AS s, question AS q, tag_question AS tq WHERE t.site_id = s.site_id AND q.site_id = s.site_id AND tq.site_id = s.site_id AND tq.question_id = q.id AND tq.tag_id = t.id AND s.site_name IN ('stackoverflow') AND t.name IN ('.net-4.5', 'android-manifest', 'android-volley', 'asp.net-core-2.0', 'coffeescript', 'cucumber', 'google-plus', 'histogram', 'linear-algebra', 'smtp', 'threadpool', 'tinymce', 'treeview') AND q.favorite_count >= 1 AND q.favorite_count <= 10