SELECT COUNT(*) FROM tag AS t, site AS s, question AS q, tag_question AS tq WHERE t.site_id = s.site_id AND q.site_id = s.site_id AND tq.site_id = s.site_id AND tq.question_id = q.id AND tq.tag_id = t.id AND s.site_name IN ('stackoverflow') AND t.name IN ('filepath', 'genymotion', 'i2c', 'java-web-start', 'nsdata', 'popover', 'profiler', 'python-module', 'tensor', 'windows-server-2003') AND q.score >= 0 AND q.score <= 1000