SELECT COUNT(*) FROM tag AS t, site AS s, question AS q, tag_question AS tq WHERE t.site_id = s.site_id AND q.site_id = s.site_id AND tq.site_id = s.site_id AND tq.question_id = q.id AND tq.tag_id = t.id AND s.site_name IN ('pt') AND t.name IN ('ajax', 'angularjs', 'array', 'c++', 'css3', 'delphi', 'entity-framework', 'json', 'laravel', 'orientação-a-objetos', 'python-3.x', 'r', 'string') AND q.score >= 1 AND q.score <= 10