SELECT COUNT(*) FROM tag AS t, site AS s, question AS q, tag_question AS tq WHERE t.site_id = s.site_id AND q.site_id = s.site_id AND tq.site_id = s.site_id AND tq.question_id = q.id AND tq.tag_id = t.id AND s.site_name IN ('stackoverflow') AND t.name IN ('angularjs-ng-repeat', 'angularjs-scope', 'apache-camel', 'google-calendar-api', 'gorm', 'graph-algorithm', 'hex', 'itextsharp', 'jsonp', 'kivy', 'office-js', 'postman', 'single-page-application', 'tcl') AND q.favorite_count >= 0 AND q.favorite_count <= 10000