SELECT COUNT(*) FROM tag AS t, site AS s, question AS q, tag_question AS tq WHERE t.site_id = s.site_id AND q.site_id = s.site_id AND tq.site_id = s.site_id AND tq.question_id = q.id AND tq.tag_id = t.id AND s.site_name IN ('salesforce') AND t.name IN ('lightning', 'lightning-aura-components', 'soql', 'unit-test') AND q.view_count >= 0 AND q.view_count <= 100