SELECT COUNT(*) FROM tag AS t, site AS s, question AS q, tag_question AS tq WHERE t.site_id = s.site_id AND q.site_id = s.site_id AND tq.site_id = s.site_id AND tq.question_id = q.id AND tq.tag_id = t.id AND s.site_name IN ('gis') AND t.name IN ('arcgis-10.1', 'arcmap', 'arcobjects', 'geoserver', 'openlayers-2', 'openstreetmap', 'polygon', 'python', 'r', 'wms') AND q.view_count >= 100 AND q.view_count <= 100000