SELECT COUNT(*) FROM tag AS t, site AS s, question AS q, tag_question AS tq WHERE t.site_id = s.site_id AND q.site_id = s.site_id AND tq.site_id = s.site_id AND tq.question_id = q.id AND tq.tag_id = t.id AND s.site_name IN ('sharepoint') AND t.name IN ('2007', 'client-object-model', 'document-library', 'infopath', 'jquery', 'office-365', 'rest', 'search', 'web-part') AND q.score >= 1 AND q.score <= 10