SELECT COUNT(*) FROM tag AS t, site AS s, question AS q, tag_question AS tq WHERE t.site_id = s.site_id AND q.site_id = s.site_id AND tq.site_id = s.site_id AND tq.question_id = q.id AND tq.tag_id = t.id AND s.site_name IN ('android') AND t.name IN ('4.4-kitkat', '5.0-lollipop', '6.0-marshmallow', 'root-access', 'samsung', 'updates') AND q.score >= 0 AND q.score <= 1000