SELECT COUNT(*) FROM tag AS t, site AS s, question AS q, tag_question AS tq WHERE t.site_id = s.site_id AND q.site_id = s.site_id AND tq.site_id = s.site_id AND tq.question_id = q.id AND tq.tag_id = t.id AND s.site_name IN ('physics') AND t.name IN ('astronomy', 'capacitance', 'charge', 'friction', 'gauge-theory', 'group-theory', 'hamiltonian-formalism', 'heisenberg-uncertainty-principle', 'notation', 'nuclear-physics', 'quantum-spin', 'supersymmetry', 'vectors', 'velocity', 'voltage') AND q.view_count >= 10 AND q.view_count <= 1000