SELECT COUNT(*) FROM tag AS t, site AS s, question AS q, tag_question AS tq WHERE t.site_id = s.site_id AND q.site_id = s.site_id AND tq.site_id = s.site_id AND tq.question_id = q.id AND tq.tag_id = t.id AND s.site_name IN ('mathematica') AND t.name IN ('dynamic', 'export', 'fitting', 'front-end', 'import', 'linear-algebra', 'manipulate', 'simplifying-expressions', 'special-functions', 'symbolic') AND q.view_count >= 100 AND q.view_count <= 100000